import React from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Grid,Row,Col} from 'react-flexbox-grid';
import AppBar from '@material-ui/core/AppBar';
import Typography from '@material-ui/core/Typography';
import Toolbar from '@material-ui/core/Toolbar';
import TextField from '@material-ui/core/TextField';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import {Table,Modal,ModalHeader,ModalBody,ModalFooter} from 'reactstrap';
import axios from 'axios';
import moment from 'moment';

const url = "https://rayentutorialtestapp.azurewebsites.net/tutorials";

/*
{
        "id": 1880,
        "nombre": "Titulo Tester",
        "profesor": "Profesor Test",
        "materia": "Materia tester",
        "fecha": "2020-09-19T00:00:00.000Z",
        "materia_id": null
    },
*/

class App extends React.Component {


  state = {
    data: [],
    isLoad: false,
    order: 'titulo'
  };

  obtenerTutoriales(){
    axios.get(url).then(response=>{ 
      this.setState({data: response.data});
    })
  }


  componentDidMount(){
    this.obtenerTutoriales();
  }

  cambiarFormatoFecha(fecha){
    var formato = "";
    var dia = moment.unix(fecha).utc().daysInMonth();
    var mes = moment.unix(fecha).utc().month();
    var ano = moment.unix(fecha).utc().year();
    formato = dia +" "+ mes +" "+ ano;
    return formato
  }


  render(){
    return <>
      <Grid>
        <Row>
          <AppBar position="sticky">
            <Toolbar>
              <Typography variant="h4" color="inherit">
                Tutoriales
              </Typography>
            </Toolbar>
          </AppBar>
        </Row>
        <Row>
          <br/>
        </Row>
        <Row>
          <Col xs={12} md={12}>          
            <TextField   
              id="buscador" 
              variant="outlined"
              label="Buscar por título" 
              style={{ margin: 8 }}
              fullWidth
              margin="normal"/>          
          </Col>          
        </Row>
        <Row>
          <Col xs={4} md={4}>

          </Col>
          <Col xs={4} md={4}>
            Ordenado por
          </Col>
          <Col xs={4} md={4}>
            <Select
              labelId="selOrden"
              id="selOrden"      
              value={this.state.order}       
            >
              <MenuItem value={'titulo'}>Título</MenuItem>
              <MenuItem value={'fecha'}>Fecha</MenuItem>
            </Select>
          </Col>
        </Row>
        {this.state.data.map(elemento=>(
          <Row>
            <Col xs={6} md={6}>
              <h6>{elemento.nombre}</h6>
            </Col>
            <Col xs={6} md={6}>
              {this.cambiarFormatoFecha()} 
            </Col>
          </Row>

        ))}
        <Row>
          Eliminar Todo
        </Row>
      </Grid>
    </>;
  }
}

export default App;
